import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';

const Home = () => {
  const [query, setQuery] = useState('');

  const handleSearch = (q) => {
    setQuery(q);
  };

  // Inline styles for the home component
  const homeStyle = {
    backgroundImage: 'url(https://images.unsplash.com/photo-1558449033-15551587c9f3?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80)', // Direct image link from Unsplash
    backgroundSize: 'cover',    // Ensures the background covers the entire area
    backgroundPosition: 'center', // Centers the image
    backgroundRepeat: 'no-repeat', // Prevents tiling
    height: '100vh',  // Full viewport height
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#fff', // White text for contrast
    textAlign: 'center',
    padding: '0 20px' // Padding for small screens
  };

  // Inline styles for heading
  const headingStyle = {
    fontSize: '3rem',
    marginBottom: '20px'
  };

  return (
    <div style={homeStyle}>
      <h1 style={headingStyle}>Welcome to Prestige Properties</h1>
      <SearchBar onSearch={handleSearch} />
      {/* Add more content like featured properties */}
    </div>
  );
}

export default Home;
